(70, 'woocommerce-payfast-may-2024-update-needed', 'update', 'en_US', 'An important update is required for the Payfast Payment Gateway', '<strong>Please update to the latest version of the Payfast Payment Gateway for WooCommerce immediately to address a security vulnerability</strong>. This update is required to ensure that your transactions continue to process.', '{}', 'pending', 'woocommerce.com', '2024-05-13 19:19:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'mailchimp_q2_2024', 'marketing', 'en_US', 'Level up your marketing to boost sales', 'Looking for a smart way to kickstart your growth? Mailchimp’s best-in-class email marketing empowers you to create and automate precisely targeted SMS and email campaigns and build long-term relationships with your audience. Enhance your customer experience, track sales, and more by connecting your store to Mailchimp.', '{}', 'pending', 'woocommerce.com', '2024-06-05 01:25:10', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'WC-order-attribution-update-June-2024', 'update', 'en_US', 'An important update is required for WooCommerce', '<strong>An important security update for WooCommerce has been released</strong>. It is recommended that you update as quickly as possible.', '{}', 'unactioned', 'woocommerce.com', '2024-06-11 00:40:21', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'checkout_block_update_july_2024', 'marketing', 'en_US', 'UPDATE: The checkout block is getting an upgrade!', 'We’re improving the checkout block in 2024, starting with WooCommerce 9.0, launching on June 18! The latest release includes streamlined order summary with sticky functionality, better address form layout, and other enhancements to boost conversion.', '{}', 'pending', 'woocommerce.com', '2024-06-18 19:32:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'bfcm-survey-jul2024', 'marketing', 'en_US', 'Complete our holiday survey and get a $30 coupon', 'We want to understand how you prepare your store for Black Friday/Cyber Monday and the holidays.\r\n\r\nComplete our 10-minute survey and get $30 USD off an extension from our official Marketplace. Hurry, the survey closes July 15.', '{}', 'pending', 'woocommerce.com', '2024-07-04 19:12:35', NULL, 0, 'plain', '', 0, 0, 'info'),
(75, 'stripe_newcheckout_q3_2024', 'marketing', 'en_US', 'Upgrade your Stripe checkout for even more payment options', 'Boost sales, increase order value, and reach new customers with the new checkout from Stripe. Support 23 payment methods, including buy now, pay later methods like Klarna, Afterpay, and Affirm; Bancontact in Europe and Alipay in Asia; and Link, an accelerated checkout experience that autofills payment information.', '{}', 'pending', 'woocommerce.com', '2024-07-04 19:12:35', NULL, 0, 'plain', '', 0, 0, 'info'),
(76, 'klaviyo_q4_2024', 'marketing', 'en_US', 'Need a marketing solution to help you sell more?', 'Over 13,000 brands use Klaviyo to grow faster. You can drive 46x ROI with their personalized email, SMS, forms, and product reviews. Start for free today.', '{}', 'pending', 'woocommerce.com', '2024-07-31 19:26:34', NULL, 0, 'plain', '', 0, 0, 'info'),
